INSERT INTO `cscart_product_descriptions` VALUES (118,'ru','Тайны Смолвиля: 10 сезон','','','<p><span style=\"font-size: small;\">1989 год, метеоритный дождь в городе Смолвиль наносит невиданные разрушения, а в бездетной семье жителей Смолвиля странным образом появляется маленький ребёнок &mdash; Кларк Кент.</span></p><p><span style=\"font-size: small;\"> Прошло много лет. Кларк уже учится в школе. Его ошеломляющие способности заставляют его чаще задуматься о том, кто он собственно такой. К тому же он влюблен в одноклассницу &mdash; Лану Лэнг, чьи родители пропали во время метеоритного дождя. Этот фактор значительно осложняет их отношения. Внимательные родители Кларка пытаются любым путём сберечь его секрет, даже возбраняют ему выступать в школьной футбольной команде.</span></p><p><span style=\"font-size: small;\"> Однажды Кларк спас жизнь молодого бизнесмена Лекса Лютера, когда машина бизнесмена, потеряв управление, падает с моста в реку. С этого обстоятельства и завязывается череда многообразных похождений супермена &mdash; Кларка Кента.</span></p>','','','','','','<p class=\"product-promo-text\"><span class=\"product-promo-header\">БЕСПЛАТНАЯ доставка на заказ свыше 3000 рублей!</span><span class=\"product-promo-body\">Отправка заказа в течение суток.</span></p>');
