INSERT INTO `cscart_product_descriptions` VALUES (111,'ru','Последний самурай, (DVD) (WS)','','','<p><span style=\"color: #333333; font-family: Arial, Tahoma, Helvetica, sans-serif; font-size: small;\">Действие разворачивается в Японии 70-ых годов девятнадцатого века. Капитан Нейтон Альгрен, уважаемый американский военный офицер, нанят Императором Японии для обучения первой армии Страны Восходящего Солнца современному искусству ведения боевых действий. Император пытается искоренить древних воинов-самураев, готовясь к более прозападной политике правительства&hellip;</span></p>','','','','','','<p class=\"product-promo-text\"><span class=\"product-promo-header\">БЕСПЛАТНАЯ доставка на заказ свыше 3000 рублей!</span><span class=\"product-promo-body\">Отправка заказа в течение суток.</span></p>');
