INSERT INTO `cscart_product_descriptions` VALUES (151,'ru','Wildwood classic','','','<p><span style=\"font-size: small;\">Предпочитаете активный отдых, который подразумевает под собой катание на велосипеде по городу или пересечённой местности? И, кроме того, велосипед вам нужен недорогой, но при этом очень качественный. Ваc можно только поздравить, ведь Diamondback Wildwood Classic обладает всеми необходимыми вам качествами! В его основе &ndash; прочная и сравнительно лёгкая рама, которая изготовлена из высококачественного алюминиевого сплава 6061. На раму установлены такие комплектующие, как: амортизационная вилка Comfort Suspension с ходом 50 мм, которая поможет вам без труда и лишних усилий преодолеть неровности пути на пересечённой местности; ободные тормоза типа V-Brake, обеспечивающие отличное торможение, своевременное и довольно мощное; прочные двойные ободья SSW26; навесное оборудование начального уровня Shimano Tourney, которое обеспечит чёткое переключение скоростей.</span></p>','','','','','','<p class=\"product-promo-text\"><span class=\"product-promo-header\">БЕСПЛАТНАЯ доставка на заказ свыше 3000 рублей!</span><span class=\"product-promo-body\">Отправка заказа в течение суток.</span></p>');
