INSERT INTO `cscart_product_descriptions` VALUES (232,'ru','2011 Ice Queen','','','<p><span style=\"font-size: small;\">Рюкзак Camelbak - женский рюкзак идеально подходящий для активного отдыха в зимний период времени. Общий объём 16,4 литра, что позволяет вместить необходимый багаж. В этом рюкзаке есть отделение для воды с системой, не позволяющей ей замерзнуть. Резервуар для воды 2 литра.</span></p>','','','','','','<p class=\"product-promo-text\"><span class=\"product-promo-header\">БЕСПЛАТНАЯ доставка на заказ свыше 3000 рублей!</span><span class=\"product-promo-body\">Отправка заказа в течение суток.</span></p>');
